import React, { useState, useEffect } from "react";
import "./App.css";
import SymptomInput from "./components/SymptomInput";
import ResultsDisplay from "./components/ResultsDisplay";
import Disclaimer from "./components/Disclaimer";

export default function App() {
  const [sessionId, setSessionId] = useState(null);
  const [conversation, setConversation] = useState([]);
  const [displayedMessages, setDisplayedMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  // Start a new session on first render
  useEffect(() => {
    const initSession = async () => {
      const res = await fetch("http://localhost:5001/api/start-session", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });
      const data = await res.json();
      setSessionId(data.sessionId);
    };
    initSession();
  }, []);

  // Display messages step-by-step for chat effect
  useEffect(() => {
    if (conversation.length === 0) {
      setDisplayedMessages([]);
      return;
    }

    let index = 0;
    setDisplayedMessages([]);

    const interval = setInterval(() => {
      if (index >= conversation.length) {
        clearInterval(interval);
        return;
      }
      setDisplayedMessages((prev) => [...prev, conversation[index]]);
      index++;
    }, 500);

    return () => clearInterval(interval);
  }, [conversation]);

  const handleSendMessage = async () => {
    if (!userInput.trim() || !sessionId) return;
    setConversation(prev => [...prev, { role: "user", text: userInput }]);
    setLoading(true);

    try {
      const res = await fetch("http://localhost:5001/api/next-step", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sessionId, userMessage: userInput }),
      });

      const data = await res.json();

      setConversation(prev => [
        ...prev,
        { role: "assistant", text: data.assistantMessage }
      ]);

      if (data.done) {
        // Show final results
        const finalResults = data.possible_conditions.map(cond => ({
          condition: cond,
          description: "Derived from dataset conversation",
          confidence: 60 + Math.round(Math.random() * 30)
        }));
        setResults(finalResults);
      }

    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
      setUserInput("");
    }
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Medichat Symptom Checker</h1>
      <p className="app-subtitle">
        Enter your symptoms and get a step-by-step Q&A simulation.
      </p>

      <SymptomInput
        symptoms={userInput}
        setSymptoms={setUserInput}
        onCheck={handleSendMessage}
      />

      {loading && (
        <div className="processing">
          <span className="ai-icon">ðŸ¤–</span> Thinking...
        </div>
      )}

      {displayedMessages.length > 0 && (
        <div className="conversation-box">
          {displayedMessages.map((msg, idx) => (
            <div
              key={idx}
              className={`chat-message ${msg.role === "assistant" ? "ai-message" : "user-message"}`}
            >
              {msg.role === "assistant" && <span className="ai-icon">ðŸ¤–</span>}
              {msg.role === "user" && <span className="user-icon">ðŸ§‘</span>}
              {msg.text}
            </div>
          ))}
        </div>
      )}

      {!loading && results.length > 0 && <ResultsDisplay results={results} />}

      <Disclaimer />
    </div>
  );
}
