import React, { useState, useEffect } from "react";
import "./App.css";
import SymptomInput from "./components/SymptomInput";
import ResultsDisplay from "./components/ResultsDisplay";
import Disclaimer from "./components/Disclaimer";

export default function App() {
  const [conversation, setConversation] = useState([]);
  const [displayedMessages, setDisplayedMessages] = useState([]);
  const [symptoms, setSymptoms] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  // Step-by-step rendering for chat realism
  useEffect(() => {
    if (conversation.length === 0) {
      setDisplayedMessages([]);
      return;
    }

    let index = 0;
    setDisplayedMessages([]);

    const interval = setInterval(() => {
      if (index >= conversation.length) {
        clearInterval(interval);
        return;
      }
      setDisplayedMessages((prev) => [...prev, conversation[index]]);
      index++;
    }, 600); // 0.6s per message

    return () => clearInterval(interval);
  }, [conversation]);

  const handleCheckSymptoms = async () => {
    if (!symptoms.trim()) return;
    setLoading(true);
    setConversation([]);
    setResults([]);

    try {
      const response = await fetch("http://localhost:5001/api/check-symptoms", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ symptoms }),
      });

      const data = await response.json();
      setConversation(data.conversation || []);
      setResults(data.results || []);
    } catch (err) {
      console.error("Error fetching results:", err);
      setConversation([{ role: "assistant", text: "Something went wrong. Please try again." }]);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Medichat Symptom Checker</h1>
      <p className="app-subtitle">
        Describe your symptoms naturally, and the system will guide you through a Q&A conversation.
      </p>

      <SymptomInput
        symptoms={symptoms}
        setSymptoms={setSymptoms}
        onCheck={handleCheckSymptoms}
      />

      {loading && (
        <div className="processing">
          <span className="ai-icon">ðŸ¤–</span> Processing...
        </div>
      )}

      {displayedMessages.length > 0 && (
        <div className="conversation-box">
          {displayedMessages.map((msg, idx) => (
            <div
              key={idx}
              className={`chat-message ${msg.role === "assistant" ? "ai-message" : "user-message"}`}
            >
              {msg.role === "assistant" && <span className="ai-icon">ðŸ¤–</span>}
              {msg.role === "user" && <span className="user-icon">ðŸ§‘</span>}
              {msg.text}
            </div>
          ))}
        </div>
      )}

      {!loading && results.length > 0 && <ResultsDisplay results={results} />}

      <Disclaimer />
    </div>
  );
}
