import React, { useState, useRef } from "react";
import "./SymptomInput.css";

function SymptomInput({ symptoms, setSymptoms, onCheck }) {
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);

  // ðŸŽ¤ Voice input handler
  const handleVoiceInput = () => {
    if (!("webkitSpeechRecognition" in window || "SpeechRecognition" in window)) {
      alert("Sorry, your browser does not support speech recognition.");
      return;
    }

    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();
    recognitionRef.current = recognition;

    recognition.continuous = false;
    recognition.interimResults = false;
    recognition.lang = "en-US";

    if (!isListening) {
      setIsListening(true);
      recognition.start();

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setSymptoms((prev) => (prev ? `${prev} ${transcript}` : transcript));
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      recognition.onerror = (event) => {
        console.error("Speech recognition error:", event.error);
        setIsListening(false);
      };
    } else {
      recognition.stop();
      setIsListening(false);
    }
  };

  return (
    <div className="symptom-input-container">
      <textarea
        className="symptom-textarea"
        placeholder="e.g., I have a bad cough, sore throat, and I'm very tired."
        value={symptoms}
        onChange={(e) => setSymptoms(e.target.value)}
      />

      {/* ðŸŽ¤ Mic icon */}
      <div
        className="mic-icon"
        onClick={handleVoiceInput}
        title={isListening ? "Listening..." : "Speak"}
      >
        {isListening ? (
          // ðŸ”´ Animated mic when listening
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"
            style={{ fill: "#ff0000", animation: "pulse 1s infinite" }}
          >
            <path d="M12 14a3 3 0 0 0 3-3V5a3 3 0 0 0-6 0v6a3 3 0 0 0 3 3zm5-3a5 5 0 0 1-10 0H5a7 7 0 0 0 14 0h-2zM11 18h2v3h-2v-3z" />
          </svg>
        ) : (
          // âš« Normal mic
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
            <path d="M12 14a3 3 0 0 0 3-3V5a3 3 0 0 0-6 0v6a3 3 0 0 0 3 3zm5-3a5 5 0 0 1-10 0H5a7 7 0 0 0 14 0h-2zM11 18h2v3h-2v-3z" />
          </svg>
        )}
      </div>

      <button className="check-btn" onClick={onCheck}>
        Check Symptoms
      </button>
    </div>
  );
}

export default SymptomInput;
